import React, { useState } from 'react';
import './bestp.css'; // Make sure to import your stylesheet
import Deer from "../../Assests/BestPhotoGraphy/tiger.jpg"

const ImageGallery = () => {
  const [activeIndex, setActiveIndex] = useState(null);

  const handleImageContainerHover = (index) => {
    setActiveIndex(index);
  };

  const imageContainers = [
    <img src={Deer}/>

  ];

  return (
    <div className="containerr  ml-[900px] w-[50px] space-x-9 bg-black ">
      {imageContainers.map((imageUrl, index) => (
        <div
          key={index}
          className={`image-containerr  ${activeIndex === index ? 'active shadow' : ''}`}
          onMouseEnter={() => handleImageContainerHover(index)}
          onMouseLeave={() => handleImageContainerHover(null)}
        >
          <div className="image" style={{ backgroundImage: `url(${imageUrl})` }}></div>
        </div>
      ))}
    </div>
  );
};

export default ImageGallery;
