import React, { useState } from 'react';
import './BestPhotoGraphy.css'; // Make sure to adjust the path to your CSS file

const BestPhotoGraphy = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);

  const handleHover = (index) => {
    setHoveredIndex(index);
  };

  const handleLeave = () => {
    setHoveredIndex(null);
  };

  return (
    <div>
      <div className="container">
        <div
          className={`image-container ${hoveredIndex === 0 ? 'active shadow' : ''}`}
          onMouseEnter={() => handleHover(0)}
          onMouseLeave={handleLeave}
        >
          <div className="image image-one border" style={{ backgroundImage: `url(${require("../../Assests/BestPhotoGraphy/tiger.jpg")})` }}></div>
        </div>
        <div
          className={`image-container ${hoveredIndex === 1 ? 'active shadow' : ''}`}
          onMouseEnter={() => handleHover(1)}
          onMouseLeave={handleLeave}
        >
          <div className="image image-two" style={{ backgroundImage: `url(${require("../../Assests/BestPhotoGraphy/tiger2.jpg")})` }}></div>
        </div>
        <div
          className={`image-container ${hoveredIndex === 2 ? 'active shadow' : ''}`}
          onMouseEnter={() => handleHover(2)}
          onMouseLeave={handleLeave}
        >
          <div className="image image-three" style={{ backgroundImage: `url(${require("../../Assests/BestPhotoGraphy/tiger3.png")})` }}></div>
        </div>
        <div
          className={`image-container ${hoveredIndex === 3 ? 'active shadow' : ''}`}
          onMouseEnter={() => handleHover(3)}
          onMouseLeave={handleLeave}
        >
          <div className="image image-four" style={{ backgroundImage: `url(${require("../../Assests/Gallery/Rectangle 17.png")})` }}></div>
        </div>
      </div>
    </div>
  );
};

export default BestPhotoGraphy;
