import React, { useState } from 'react';
// import './YourComponent.css'; // Make sure to import your CSS file

const BestPhotoGraphy = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);

  const handleMouseEnter = (index) => {
    setHoveredIndex(index);
  };

  const handleMouseLeave = () => {
    setHoveredIndex(null);
  };

  const imageContainerClass = (index) => {
    let classes = 'image-container';

    if (hoveredIndex === index) {
      classes += ' active';
    } else if (hoveredIndex !== null) {
      classes += ' shadow';
    }

    return classes;
  };

  return (
    <div className="container">
      {[1, 2, 3, 4].map((index) => (
        <div
          key={index}
          className={imageContainerClass(index)}
          onMouseEnter={() => handleMouseEnter(index)}
          onMouseLeave={handleMouseLeave}
        >
          <div
            className={`image image-${index}`}
            style={{
              backgroundImage: `url(https://d2lm6fxwu08ot6.cloudfront.net/img-thumbs/960w/${getImageName(index)}.jpg)`,
            }}
          ></div>
        </div>
      ))}
    </div>
  );
};

const getImageName = (index) => {
  switch (index) {
    case 1:
      return '80W9RNGZ5V';
    case 2:
      return 'FU3A68CTPB';
    case 3:
      return 'S8NAP5L7J9';
    case 4:
      return '4NHY80VHLW';
    default:
      return '';
  }
};

export default BestPhotoGraphy;
